const cloneText=container=>{const rollingTexts=container.querySelectorAll(".intro-text");rollingTexts.forEach(text=>{const data=text.dataset.text,chars=Array.from(data);for(let i=0;i<chars.length;i++){const inner=document.createElement("span");inner.classList.add("intro-text-inner"),text.appendChild(inner);const letter=document.createElement("span");letter.classList.add("intro-text-letter"),letter.textContent=chars[i],inner.appendChild(letter);for(let j=0;j<6;j++){let clone=letter.cloneNode(!0);letter.after(clone)}}}),animateText(container)},animateText=container=>{const rollingInners=container.querySelectorAll(".intro-text-inner");gsap.set(rollingInners,{y:"-700%"});const tl=gsap.timeline({paused:!0,defaults:{ease:"power4.inOut"}});tl.to(rollingInners,{duration:4,y:"0",stagger:{grid:"auto",from:"random",ease:"power4.inOut",amount:1.2}}).fromTo(".intro-text-letter",{filter:"brightness(0.5)"},{duration:3,filter:"brightness(1)",stagger:{grid:"auto",ease:"power4.inOut",amount:.8}},0),tl.play()};export const introTextEffect=container=>{cloneText(container)};